	mov bx, 0d	;set bx to 0
	mov ah, 46d		; set color to 46d =  2Eh (symbol_color = yellow, background_color = green)
	mov si, 0d		;run in the loop
	
	PRINT_CHAR:
	
		;a:
		
		mov al, [AsciiArt1+si]	;get the value saved in each place in AsciiArt1 
		mov es:[bx], ax		;print
		add si, 1d		;si = si+1 so we will go to the next place in AsciiArt1
		add bx, 2h		;bx = bx +2h so we will print next to where we have printed now
	loop PRINT_CHAR

	mov ax, 4c00h
	int 21h
end START
